{"metadata":{"kernelspec":{"language":"python","display_name":"Python 3","name":"python3"},"language_info":{"pygments_lexer":"ipython3","nbconvert_exporter":"python","version":"3.6.4","file_extension":".py","codemirror_mode":{"name":"ipython","version":3},"name":"python","mimetype":"text/x-python"}},"nbformat_minor":4,"nbformat":4,"cells":[{"cell_type":"code","source":"# %% [code] {\"execution\":{\"iopub.status.busy\":\"2023-04-11T13:48:19.609165Z\",\"iopub.execute_input\":\"2023-04-11T13:48:19.609527Z\",\"iopub.status.idle\":\"2023-04-11T13:48:19.616614Z\",\"shell.execute_reply.started\":\"2023-04-11T13:48:19.609495Z\",\"shell.execute_reply\":\"2023-04-11T13:48:19.615272Z\"}}\nimport tensorflow as tf\nfrom keras import layers\nimport pandas as pd\nimport numpy as np\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2023-04-11T13:48:19.620959Z\",\"iopub.execute_input\":\"2023-04-11T13:48:19.621520Z\",\"iopub.status.idle\":\"2023-04-11T13:48:22.617593Z\",\"shell.execute_reply.started\":\"2023-04-11T13:48:19.621493Z\",\"shell.execute_reply\":\"2023-04-11T13:48:22.616348Z\"}}\ntrain_df = pd.read_csv('/kaggle/input/digit-recognizer/train.csv')\ntest_df = pd.read_csv('/kaggle/input/digit-recognizer/test.csv')\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2023-04-11T13:48:22.619877Z\",\"iopub.execute_input\":\"2023-04-11T13:48:22.620524Z\",\"iopub.status.idle\":\"2023-04-11T13:48:22.896093Z\",\"shell.execute_reply.started\":\"2023-04-11T13:48:22.620478Z\",\"shell.execute_reply\":\"2023-04-11T13:48:22.895057Z\"}}\ny_train = train_df['label']\nX_train = train_df.drop('label', axis=1)\nx_train = X_train/255\ntest=test_df/255\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2023-04-11T13:48:22.897878Z\",\"iopub.execute_input\":\"2023-04-11T13:48:22.898282Z\",\"iopub.status.idle\":\"2023-04-11T13:48:23.103192Z\",\"shell.execute_reply.started\":\"2023-04-11T13:48:22.898239Z\",\"shell.execute_reply\":\"2023-04-11T13:48:23.101972Z\"}}\nx_train_array = np.array(x_train)\ntest_array = np.array(test)\nx_train_array = x_train_array.reshape(42000, 28, 28, 1)\ntest_array = test_array.reshape(-1, 28, 28, 1)\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2023-04-11T13:48:23.105917Z\",\"iopub.execute_input\":\"2023-04-11T13:48:23.110154Z\",\"iopub.status.idle\":\"2023-04-11T13:48:23.116361Z\",\"shell.execute_reply.started\":\"2023-04-11T13:48:23.110114Z\",\"shell.execute_reply\":\"2023-04-11T13:48:23.115284Z\"}}\ndatagen = tf.keras.preprocessing.image.ImageDataGenerator(\n    width_shift_range=0.2,\n    height_shift_range=0.2,\n    fill_mode=\"constant\",\n    \n    horizontal_flip=False,\n    vertical_flip=False,\n    \n    rotation_range=20,\n    \n    validation_split=0.2\n)\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2023-04-11T13:48:56.825037Z\",\"iopub.execute_input\":\"2023-04-11T13:48:56.825406Z\",\"iopub.status.idle\":\"2023-04-11T13:48:56.831883Z\",\"shell.execute_reply.started\":\"2023-04-11T13:48:56.825372Z\",\"shell.execute_reply\":\"2023-04-11T13:48:56.830508Z\"}}\nfrom keras.utils import np_utils\ny_train = np_utils.to_categorical(y_train)\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2023-04-11T13:50:26.743548Z\",\"iopub.execute_input\":\"2023-04-11T13:50:26.744934Z\",\"iopub.status.idle\":\"2023-04-11T13:50:27.054586Z\",\"shell.execute_reply.started\":\"2023-04-11T13:50:26.744866Z\",\"shell.execute_reply\":\"2023-04-11T13:50:27.053386Z\"}}\nfrom tensorflow import keras\nfrom keras.models import Sequential\nfrom keras.layers import Dense, Dropout, Flatten\nfrom keras.layers import Conv2D, MaxPooling2D, BatchNormalization\nfrom keras.callbacks import EarlyStopping\n\ncnn1 = Sequential()\ncnn1.add(Conv2D(32, kernel_size=(3, 3), activation='relu', input_shape=(28, 28, 1)))\ncnn1.add(BatchNormalization())\n\ncnn1.add(Conv2D(32, kernel_size=(3, 3), activation='relu'))\ncnn1.add(BatchNormalization())\ncnn1.add(MaxPooling2D(pool_size=(2, 2)))\ncnn1.add(Dropout(0.25))\n\ncnn1.add(Conv2D(64, kernel_size=(3, 3), activation='relu'))\ncnn1.add(BatchNormalization())\ncnn1.add(Dropout(0.25))\n\ncnn1.add(Conv2D(128, kernel_size=(3, 3), activation='relu'))\ncnn1.add(BatchNormalization())\ncnn1.add(MaxPooling2D(pool_size=(2, 2)))\ncnn1.add(Dropout(0.25))\ncnn1.add(Flatten())\n\ncnn1.add(Dense(128, activation='relu'))\ncnn1.add(BatchNormalization())\ncnn1.add(Dropout(0.5))\n\ncnn1.add(Dense(10, activation='softmax'))\n\nearly_stopper = EarlyStopping(monitor='loss', patience=3)\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2023-04-11T14:05:45.352049Z\",\"iopub.execute_input\":\"2023-04-11T14:05:45.352997Z\",\"iopub.status.idle\":\"2023-04-11T14:05:45.367661Z\",\"shell.execute_reply.started\":\"2023-04-11T14:05:45.352943Z\",\"shell.execute_reply\":\"2023-04-11T14:05:45.366411Z\"}}\ncnn1.compile(loss=keras.losses.categorical_crossentropy,\n              optimizer='adam',\n              metrics=['accuracy'])\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2023-04-11T14:05:50.863879Z\",\"iopub.execute_input\":\"2023-04-11T14:05:50.864574Z\",\"iopub.status.idle\":\"2023-04-11T14:11:56.757793Z\",\"shell.execute_reply.started\":\"2023-04-11T14:05:50.864532Z\",\"shell.execute_reply\":\"2023-04-11T14:11:56.756582Z\"}}\nhistory = cnn1.fit(x_train_array,y_train,\n          epochs=100,\n          callbacks=[early_stopper],\n          validation_split=0.1,\n          initial_epoch=3)\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2023-04-11T14:33:58.887242Z\",\"iopub.execute_input\":\"2023-04-11T14:33:58.888048Z\",\"iopub.status.idle\":\"2023-04-11T14:34:15.518051Z\",\"shell.execute_reply.started\":\"2023-04-11T14:33:58.888008Z\",\"shell.execute_reply\":\"2023-04-11T14:34:15.516847Z\"}}\nfrom matplotlib import pyplot as plt\nplt.plot(history.history['loss'])\nplt.plot(history.history['val_loss'])\nplt.title('model accuracy')\nplt.ylabel('accuracy')\nplt.xlabel('epoch')\nplt.legend(['train', 'val'], loc='upper left')\nplt.show()\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2023-04-11T14:34:48.931053Z\",\"iopub.execute_input\":\"2023-04-11T14:34:48.931975Z\",\"iopub.status.idle\":\"2023-04-11T14:34:52.272203Z\",\"shell.execute_reply.started\":\"2023-04-11T14:34:48.931921Z\",\"shell.execute_reply\":\"2023-04-11T14:34:52.271069Z\"}}\ny_test1 = cnn1.predict(test_array)\ny_test1 = np.array(pd.DataFrame(y_test1).idxmax(axis=1))\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2023-04-11T14:36:32.843166Z\",\"iopub.execute_input\":\"2023-04-11T14:36:32.844162Z\",\"iopub.status.idle\":\"2023-04-11T14:36:32.886628Z\",\"shell.execute_reply.started\":\"2023-04-11T14:36:32.844122Z\",\"shell.execute_reply\":\"2023-04-11T14:36:32.885275Z\"}}\ndf_submission = pd.read_csv('/kaggle/input/digit-recognizer/sample_submission.csv')\ndf_submission['Label']=y_test1\ndf_submission.to_csv('submission.csv',index=False)","metadata":{"_uuid":"74bc1b2c-9e56-4d52-a122-18c2183dcb0a","_cell_guid":"8f44bd71-ca1c-4880-bf15-2bfb131e1b91","collapsed":false,"jupyter":{"outputs_hidden":false},"trusted":true},"execution_count":null,"outputs":[]}]}